import logo from './logo.svg';
import './App.css';


import React, { useState, useEffect, Component } from "react";
import axios from 'axios'

const api = axios.create({ "baseURL":'' });

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
			tableList: [
				// {
				// 	'storyDescription': 'Create the spec sheet for the customer meeting', 
				// 	'jobType': 'Requirements', 
				// 	'developerRole': 'Business Analyst', 
				// 	'storypoints': 8, 
				// 	'Hours': 20, 
				// 	'HourlyRate': 90, 
				// 	'Cost': 1800, 
				// 	'numberOfPeople': 1 
				// },
				// {
				// 	'storyDescription': 'Push the code into the TFS pipe and run the ML code', 
				// 	'jobType': 'Pipeline Push', 
				// 	'developerRole': 'Machine learning Engineer', 
				// 	'storypoints': 13, 
				// 	'Hours': 25, 
				// 	'HourlyRate': 210, 
				// 	'Cost': 5250, 
				// 	'numberOfPeople': 2 
				// }
			],
			row: 
			{

			},
			default: {
				'storyDescription': 'Create the spec sheet for the customer meeting', 
				'jobType': 'Requirements', 
				'developerRole': 'Business Analyst', 
				'storypoints': 8, 
				'Hours': 20, 
				'HourlyRate': 90, 
				'Cost': 1800, 
				'numberOfPeople': 1 
			}
		}

		this.getUserStory = this.getUserStory.bind(this);
		this.Add = this.Add.bind(this);
		this.Delete = this.Delete.bind(this);
		this.Predict = this.Predict.bind(this);
	}

	getUserStory(e) {
		let user_story = e.currentTarget.value;
		let data = { 
			'storyDescription': user_story, 
			'jobType': '', 
			'developerRole': '', 
			'storypoints': 0, 
			'Hours': 0, 
			'HourlyRate': 0, 
			'Cost': 0, 
			'numberOfPeople': 0 
		}

		return data;
	}


	Add() {
		let row = this.state.row;
		let newTable = this.state.tableList;
		newTable.push(row);
		this.setState({ tableList: newTable });
		alert(JSON.stringify(this.state.tableList))
	}

	Delete(e) {
		let parent = e.currentTarget.parentElement;
		let index = e.currentTarget.getAttribute('index');
		let data = this.state.tableList;
		//data.splice(index, 1);
		this.setState({ tableList: data });
	}

	Predict(e) {
		if (e.key == 13 || e.which == 13) {
			let user_story = e.currentTarget.value;
			let row = this.state.default;
			row.storyDescription = user_story;

			this.setState({ row: row });
			//this.Add();
		}
	}


    render() {

		let tableStyle = {
			width: "100%",
			borderRadius: 10,
			boxShadow: '0px px 10px 10px #00000010',
			border: "1px solid #cecece",
			backgroundColor: "#ece9e9"
		}

		let total_storypoints = this.state.tableList.reduce((a, b) => a.storypoints + b.storypoints) ?? 0;
		let total_hours = this.state.tableList.reduce((a, b) => a.Hours + b.Hours) ?? 0;
		let avg_hourly_rate = this.state.tableList.reduce((a, b) => a.HourlyRate + b.HourlyRate) / (this.state.tableList.length = this.state.tableList.lengh > 0 ? this.state.tableList : 1) ?? 0;
		let total_cost = this.state.tableList.reduce((a, b) => a.Cost + b.Cost) ?? 0;
		let total_no_people = this.state.tableList.reduce((a, b) => a.numberOfPeople + b.numberOfPeople) ?? 0;

    	return (

			<div className="App">
				{this.state.row && Object.keys(this.state.row).includes("storyDescription") && this.state.row.storyDescription != ""  &&
					<div style={{ left: 70, top: 25, position: 'absolute', width: "fint-content", display: "inline-flex" }}>
						<div style={{fontSize: 30, fontStyle: "san serif", fontWeight: 600, marginBottom: 30, paddingRight: 40 }}>
							<span style={{ color: '#4285f4' }}>L</span>
							<span style={{ color: '#ea4335' }}>o</span>
							<span style={{ color: '#fbbc05' }}>o</span>
							<span style={{ color: '#4285f4' }}>g</span>
							<span style={{ color: '#34a853' }}>l</span>
							<span style={{ color: '#ea4335' }}>e</span>
						</div>
					<div>
						<input style={{ width: "50%", left: "25%", maxWidth: 500, minWidth: 500, height: 30, borderRadius: 40, 
							boxShadow: '0px 5px 2px 0px #00000010', padding: 10, paddingLeft: 20, paddingRight: 20,
							fontSize: 20, border: "1px solid #cecece"
							}} type="text" placeholder="Chat user stories ..." />

						</div>
				</div>}

				{(!Object.keys(this.state.row).includes("storyDescription") || this.state.row.storyDescription == "") && 
				<div style={{ top: "25%", position: 'absolute', width: "100%" }}>
					<div style={{fontSize: 80, fontStyle: "san serif", fontWeight: 600, marginBottom: 30 }}>
					<span style={{ color: '#4285f4' }}>L</span>
					<span style={{ color: '#ea4335' }}>o</span>
					<span style={{ color: '#fbbc05' }}>o</span>
					<span style={{ color: '#4285f4' }}>g</span>
					<span style={{ color: '#34a853' }}>l</span>
					<span style={{ color: '#ea4335' }}>e</span>
					</div>
					<div>
						<input style={{ width: "50%", left: "25%", maxWidth: 600, minWidth: 400, height: 30, borderRadius: 40, 
							boxShadow: '0px 5px 1px 0px #00000010', padding: 10, paddingLeft: 20, paddingRight: 20,
							fontSize: 20, border: "1px solid #cecece"
							}}
							onKeyUp ={this.Predict}
							 type="text" 
							 placeholder="Chat user stories ..." />

						<div style={{ fontSize: 10, fontWeight: 'normal', position: "absolute", right: 0,
								width: "50%", left: "25%", maxWidth: 600, minWidth: 400 }}>
									powered by Frank Frisby and data source: pretrained GPT 3. model (used API)
						</div>
					</div>
				</div>}

				<div style={{ top: 80, position: "absolute", width: "70%", padding: "5%", paddingLeft: 200, paddingRight: "15%" }}>
					{(this.state.row && Object.keys(this.state.row).includes("storyDescription") && this.state.row.storyDescription != "") && 
					<div className='story-table' >
						<table className="table" style={tableStyle}>
							<thead style={{ }}>
								<tr>
									<th>User Story</th>
									<th>Job Type</th>
									<th>Role</th>
									<th>Story Points</th>
									<th>Hours</th>
									<th>Hourly Rate</th>
									<th>Cost</th>
									<th># People</th>
								</tr>
							</thead>
							<tbody style={{ backgroundColor: "#ffffff" }}>
								<tr>
									<td style={{ fontWeight: "bold", textAlign: "left", padding: 8, paddingLeft: 13 }}>{this.state.row.storyDescription}</td>
									<td style={{ textAlign: "left", padding: 8, paddingLeft: 13 }}>{this.state.row.jobType}</td>
									<td style={{ textAlign: "left", padding: 8, paddingLeft: 13 }}>{this.state.row.developerRole}</td>
									<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{this.state.row.storypoints}</td>
									<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{this.state.row.Hours}</td>
									<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>${this.state.row.HourlyRate}</td>
									<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>${this.state.row.Cost}</td>
									<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{this.state.row.numberOfPeople}</td>
									<td style={{ textAlign: "center", padding: 8, paddingLeft: 13, 
											color: "#ffffff", backgroundColor: "green", borderRadius: 8 }} 
										onClick={this.Add}>
											Add
									</td>
								</tr>
							</tbody>
						</table>
					</div>}

					{this.state.tableList && this.state?.tableList.length > 0 && <div className='story-table' >
					<h2 style={{ textAlign: "left", marginTop: 60 }}>Project Management List</h2>
					<table className="table" style={tableStyle}>
							<thead style={{ }}>
								<tr>
									<th>User Story</th>
									<th>Job Type</th>
									<th>Role</th>
									<th>Story Points</th>
									<th>Hours</th>
									<th>Hourly Rate</th>
									<th>Cost</th>
									<th># People</th>
								</tr>
							</thead>
							<tbody style={{ backgroundColor: "#ffffff" }}>
								{this.state?.tableList.map((row, index) => { 
									return <tr>
										<td style={{ fontWeight: "bold", textAlign: "left", padding: 8, paddingLeft: 13 }}>{row.storyDescription}</td>
										<td style={{ textAlign: "left", padding: 8, paddingLeft: 13 }}>{row.jobType}</td>
										<td style={{ textAlign: "left", padding: 8, paddingLeft: 13 }}>{row.developerRole}</td>
										<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{row.storypoints}</td>
										<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{row.Hours}</td>
										<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>${row.HourlyRate}</td>
										<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>${row.Cost}</td>
										<td style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{row.numberOfPeople}</td>									
										<td style={{ textAlign: "center", padding: 8, paddingLeft: 13, 
											 fontSize: 30, borderRadius: 8 }} 
											index={index}
											onClick={this.Delete}>
											&times;
										</td>
									</tr>
								})}
							</tbody>
							<tfoot>
								<tr>
									<th></th>
									<th></th>
									<th></th>
									<th style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{total_storypoints}</th>
									<th style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{total_hours}</th>
									<th style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>${avg_hourly_rate}</th>
									<th style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>${total_cost}</th>
									<th style={{ textAlign: "center", padding: 8, paddingLeft: 13 }}>{total_no_people}</th>
								</tr>
							</tfoot>
						</table>
					</div>}
				</div>
				<div style={{ minHeight: 50, maxHeight: 100, padding: 20, fontSize: 12, display: "inline-flex", position: "absolute", bottom: 0, left: 180, bacgroundColor: "#ece9e9" }}>
					<div style = {{ margingRight: 20 }}>App is Built with React</div>
					<div style = {{ margingRight: 20 }}>API call is Azure Cloud function</div>
					<div style = {{ margingRight: 20 }}>App is hosted on Azure web app</div>
					<div style = {{ margingRight: 20 }}>AI is written in python using Tensorflow</div>
				</div>
			</div>
        );
	}
}

export default App;
