  TOKEN_STORAGE_NAME,
  TRANSACTION_STORAGE_NAME,
  SHARED_TRANSACTION_STORAGE_NAME,
  ORIGINAL_URI_STORAGE_NAME,
  IDX_RESPONSE_STORAGE_NAME,
  CACHE_STORAGE_NAME,
  REDIRECT_OAUTH_PARAMS_NAME
} from './constants';
